import React, { FunctionComponent } from 'react'; // importing FunctionComponent
import './Tile.css';

type TileProps = {
    letter: string
    highlight: boolean
}

export const Tile = ({ letter, highlight}: TileProps) =>
    <div className={(!highlight ? "tile_wrapper" : "tile_wrapper highlight")+
    (label!=' '?" letter":" l"+letterMultiplier+wordMultiplier)}>
        <div className="noselect tile_label">
           {label}
        </div>
        <div className = "noselect tile_score">
            {label!=' '?3:""}
        </div>
    </div>
