import React, {FunctionComponent, ReactNode} from 'react'; // importing FunctionComponent
import './Square.css';
import {Tile} from "./Tile";
type SquareProps = {
    letter: string
    wordMultiplier: number
    letterMultiplier: number
    highlight: boolean
}

export const Square = ({letter,letterMultiplier, wordMultiplier, highlight}: SquareProps) =>
    <div className={"square_wrapper "+squareType(letterMultiplier, wordMultiplier)}>
            {letter!=' ' ? <Tile letter = {letter} highlight={false}/> : ""}
    </div>

let squareType = (letterMultiplier: number, wordMultiplier: number): string => {
    if(wordMultiplier==3)
        return "word_3x";
    else if(wordMultiplier==2)
        return "word_2x"
    else if(letterMultiplier==3)
        return "letter_3x"
    else if(letterMultiplier==2)
        return "letter_2x"
    else
        return "none"
}
