import React, { FunctionComponent } from 'react'; // importing FunctionComponent
import './Board.css';
import {Tile} from "./Tile";

type BoardProps = {
    board : string[][]
}

export const Board: FunctionComponent<BoardProps> = ({board}) =>
    <div className="tileGrid">
        {Array.from(Array(9).keys()).map((key) =>
            <div key = {key} id = {""+key} className="tileContainer">
                <Tile key ={key+"_tile"} label={board[Math.floor(key/3)][key%3]}
                      highlight={false} />
            </div>)}
    </div>
