import React, {FunctionComponent, ReactNode} from 'react'; // importing FunctionComponent
import './Square.css';
import './Tile';
type SquareProps = {
    wordMultiplier: number
    letterMultiplier: number
    highlight: boolean
    tile: ReactNode
}

export const Square = ({highlight, letterMultiplier, wordMultiplier}: SquareProps) =>
    <div className="square_wrapper">
        <div className={squareType(letterMultiplier, wordMultiplier)}>
        </div>
    </div>

let squareType = (letterMultiplier: number, wordMultiplier: number): string => {
    if(wordMultiplier==3)
        return "word_3x";
    else if(wordMultiplier==2)
        return "word_2x"
    else if(letterMultiplier==3)
        return "letter_3x"
    else if(letterMultiplier==2)
        return "letter_2x"
    else
        return "none"
}
